import { useEffect, useState } from "react";
import axios from 'axios'
import { Line } from 'react-chartjs-2';

function App() {

  const [xAxis, setXAxis] = useState([])
  const [yAxis, setYAxis] = useState([])

  const fetchXAxis = async () => {
    try {
      const response = await axios.get('https://retoolapi.dev/gDa8uC/data')
      setXAxis(response.data)
    }catch(err) {
      console.log(err)
      alert(err.message)
    } 
  }

    const labels = xAxis.map(point => point.Label);
    const randomNumbers = xAxis.map(point => parseFloat(point.RandomNumber));

    const data = {
      labels: labels,
      datasets: [
        {
          label: 'Random Numbers',
          data: randomNumbers,
          fill: false,
          backgroundColor: 'rgb(75, 192, 192)',
          borderColor: 'rgba(75, 192, 192, 0.2)',
        },
      ],
    };

    const options = {
      scales: {
        xAxes: [{
          ticks: {
            autoSkip: true,
            maxTicksLimit: 10
          }
        }]
      }
    };

  const fetchYAxis = async () => {
    try {
      const response = await axios.get('https://retoolapi.dev/o5zMs5/data')
      setYAxis(response.data)
    }catch(err) {
      console.log(err)
      alert(err.message)
    } 
  }

  useEffect(() => {
    fetchXAxis()
    fetchYAxis()
  }, [])
  return (
    <div className="App">
      <Line data={data} options={options} />
    </div>
  );
}

export default App;
